print("start")
print("take 2")
# print(min_shifts)
avail, names, min_shifts, max_hours = ams.createAvailMatrix("Mashgiach hours optimization algorithm.xlsx")
print(min_shifts)
print(max_hours)
print(names)
# --- Solver Setup ---
print("setup")
solver = pywraplp.Solver.CreateSolver("SCIP")
MIN_SHIFT_LEN = 6

# Decision variables: x[e][t] ∈ {0,1}

x = {}
for e in range(num_employees):
    print("in prog", e)
    for t in range(num_slots):
        x[e,t] = solver.IntVar(0, 1, f"x[{e},{t}]")

# Shift start variables: y[e,t] ∈ {0,1}
y = {}
for e in range(num_employees):
    for t in range(num_slots):
        y[e,t] = solver.IntVar(0, 1, f"y[{e},{t}]")

# Constraints:
# 1) Each slot can have at most one worker
for t in range(num_slots):
    print("slot", t)
    solver.Add(sum(x[e,t] for e in range(num_employees)) <= 1)

# 2) Respect availability
for e in range(num_employees):
    for t in range(num_slots):
        # print("avail", e, t)
        if avail[e][t] == 0:
            solver.Add(x[e,t] == 0)

# (Optional) 3) Max hours per employee
for e in range(num_employees):
    solver.Add(sum(x[e,t] for t in range(num_slots)) <= max_hours[e])
    
for e in range(num_employees):
    solver.Add(sum(x[e,t] for t in range(num_slots)) >= min_shifts[e])
# (3) Minimum shift length: if y[e,t] = 1, then next MIN_SHIFT slots must be worked
for e in range(num_employees):
    # print(e)
    for t in range(num_slots - MIN_SHIFT_LEN + 1):
        for k in range(MIN_SHIFT_LEN):
            solver.Add(x[e,t+k] >= y[e,t])


# (4) Prevent invalid shift starts near the end of each day
for e in range(num_employees):
    # print(e)
    for i in range(4):
        for t in range(num_slots - MIN_SHIFT_LEN + 1 - 39*i, num_slots - 39*i):
            solver.Add(y[e,t] == 0)


# (5) Every worked slot must belong to some shift start within last MIN_SHIFT slots
for e in range(num_employees):
    # print(e)
    for t in range(num_slots):
        valid_starts = [
            y[e,s] for s in range(max(0, t-MIN_SHIFT_LEN+1), t+1)
        ]
        solver.Add(x[e,t] <= sum(valid_starts))

# Objective: Maximize total filled slots
solver.Maximize(sum(x[e,t] for e in range(num_employees) for t in range(num_slots)))

# --- Solve ---
status = solver.Solve()
# --- Output ---
schedule = []
mon = []
tues = []
wed = []
thurs = []
if status == pywraplp.Solver.OPTIMAL:
    print("Optimal schedule found!\n")
    for t in range(num_slots):
        # print(t)
        filled = False
        for e in range(num_employees):
            # print(x[e,t].solution_value())
            if x[e,t].solution_value() > 0.5:
                # print(f"Time slot {t}: Employee {e}")
                filled = True
                if t  + 1< 40:
                    mon.append(e)
                elif t + 1 < 39 * 2 + 1:
                    tues.append(e)
                elif t + 1 < 39 * 3 + 1:
                    wed.append(e)
                else:
                    thurs.append(e)
        if not filled:
                if t  + 1< 40:
                    mon.append("NULL")
                elif t + 1 < 39 * 2 + 1:
                    tues.append("NULL")
                elif t + 1 < 39 * 3 + 1:
                    wed.append("NULL")
                else:
                    thurs.append("NULL")            
    print(mon)
    print(tues)
    print(wed)
    print(thurs)
    ams.scheduleToExcel(mon, tues, wed, thurs, names)
    print(89789692 % 3)
else:
    print("No solution found.")
